/* -------------------------------- */
/*    Database client connection    */
/* -------------------------------- */

$(document).ready(() => {
    $('#form-wrapper').on('submit', (e) => {
        e.preventDefault()
        axios.post('/connect', {
            username: $('#username-field').val(),
            password: $('#password-field').val()
        }).then(({ data }) => {
            console.log(data)
            if (data.error) $('#ctr-wrapper').css({ border: "5px solid #e60f0f" })
            else window.location = '/dashboard'
        })
    })
})